import React,{useEffect,useState} from "react";
import axios from "axios";
const Animation = () => {
  const [method, setmethod] = useState('GET');
  const [url, seturl] = useState();
  const sub=()=>{
    console.log(method)
   if(method==='GET')
   {
    axios
    .get(url)
    .then((res) => {

    })
    .catch((err) => {
      console.log(err);
    });
   }
   else if(method==='POST')
   {
    axios
    .post(url)
    .then((res) => {
     console.log(res);
    })
    .catch((err) => {
      console.log(err);
    });
   }
  }
  return (
    <>
      <div className="mt-3 apicardcont container-fluid m-3 p-3 designcard">
        <div className="text-center">
          <h2 className="shadow-lg me-3 p-2">
              TEST YOUR ENDPOINT HERE
          </h2>
        </div>
      </div>
      <div className="d-flex justify-content-around container">
        <div><select style={{'fontSize':'1.3rem'}} onChange={(ev)=>{setmethod(ev.target.value)}}>
          <option value='GET'>GET</option>
          <option value='POST'>POST</option>
        </select></div>
        <div>
          <input type='text' onChange={(ev)=>{seturl(ev.target.value)}} className="form-control" style={{'width':'50rem','fontSize':'1.3rem'}}></input>
        </div>
        <div>
          <button onClick={sub} className="btn btn-primary">TEST</button>
          </div>
      </div>
      <div className="container mt-3">

      </div>
    </>
  );
};

export default Animation;
