import React, { useEffect, useState } from 'react'
import axios from 'axios';
import "../App.css"
import { useNavigate } from "react-router-dom"

const Register = () => {
  useEffect(() => {
    // const md=sessionStorage.getItem('theme')
    // document.body.className=md
  }, [])
  const history = useNavigate();
  const [User, setUser] = useState({
    name: "", uname: "", pass: "", email: ""
  });
  const getData = (ev) => {

    let name = ev.target.name;
    let value = ev.target.value;

    setUser({ ...User, [name]: value })
  }
  const sub = (ev) => {
    ev.preventDefault();
    const form = {
      name:User.name,uname: User.uname, pass: User.pass,email:User.email
    }
    axios.post("http://localhost:3001/newUser",{form}).then((res) => {
      if (res.data === "") {
        alert("enter valid data")
      }
      else {
        const users = { pic: res.data.Pic, uname: res.data.uname }
        sessionStorage.setItem("users", JSON.stringify(users));
        history('/');
      }
    }).catch((err) => {
      console.log(err);
    })
  }
  return (
    <>
      <div className='login'>
        <div className='container mt-5 child'>
          <div className='row'>
            <div className='col-md-7'>
              <div className='card'>
                <div className='text-center mt-3'>
                  <h3>CREATE NEW</h3>
                </div>
                <div className='card-body'>
                  <form>
                    <label>Name</label>
                    <input type="text" name="name" onChange={getData} value={User.name} className='form-control'></input>
                    <label className='mt-2'>User Name</label>
                    <input type="text" name="uname" onChange={getData} value={User.uname} className='form-control'></input>
                    <label className='mt-2'>Password</label>
                    <input type="password" name="pass" onChange={getData} value={User.pass} className='form-control'></input>
                    <label className='mt-2'>Email</label>
                    <input type="email" name="email" onChange={getData} value={User.email} className='form-control'></input>

                    <div className='text-center mt-3'>
                      <a href='/login'>Login</a>
                    </div>
                    <button onClick={sub} className='form-control btn btn-success mt-4'>CREATE</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default Register
