import React, { useState } from 'react'
import SetLeft from './Component/SetLeft'
import axios from 'axios';

const Setting = () => {
  const [Key,setKey]=useState('');

    const Keys=()=>{
      const pattern="abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@#$&"
       let key='';
       const keysize=30;
       for(let i=0;i<keysize;i++)
       {
          key+=pattern.charAt(Math.floor(Math.random()*pattern.length))
       }
       setKey(key);
  }
  const [AdKey, setAdKey]=useState({
    Key:""});
    const [Adappname, setAdappname]=useState({
      appname:""});

const getData=(ev)=>{

  let name=ev.target.name;
  let value=ev.target.value;

  setAdKey({...Adappname,[name]:value})
}
const sub=(ev)=>{
  ev.preventDefault();
  const form={
    AppName:AdKey.appname,Key:AdKey.key
  }

  axios.post(`${process.env.REACT_APP_BACKEND}/addKey`,form).then((res)=>{

  }).catch((err)=>{
    console.log(err);
  })
}       
  return (
    <>
      <div>
        <div className='row'>
          <div className='col-md-2 setleft'>
            <SetLeft></SetLeft>
          </div>
          <div className='col-md-10'>
            <div className='login'>
              <div className='container mt-5 keygen'>
                <div className='row'>
                  <div className='col-md-7'>
                    <div className='card'>
                      <div className='card-body mt-3'>
                        <form>
                          <label>Application Name</label>
                          <input type="text" name="appname" onChange={getData} value={AdKey.appname} className='form-control'></input>
                          <label className='mt-2'>APIs Key</label>
                          <input type="text" name="key" onClick={(ev)=>{set}} readOnly='' className='form-control' value={Key}></input>
                          <div className='genkey'><a  onClick={Keys}>Generate Key</a></div>
                          <button onClick={sub} className='form-control btn btn-success mt-4'>CREATE</button>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default Setting
