import React, { useState, useEffect } from "react";
import axios from "axios";

const AddapiData = () => {
  const [apis, setapis] = useState([]);
  const [Api, setApi] = useState();
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_BACKEND}/getapi`)
      .then((res) => {
        setapis(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  });
  const [ApiImage, setApiImage] = useState();

  const getData = (ev) => {
    setApi(ev.target.value);
  };
  const sub = (ev) => {
    ev.preventDefault();
    let form = new FormData();
    form.set("Category", Api);
    form.set("ApiImage", ApiImage);

    const config = {
      headers: { "Content-type": "multipart/form-data" },
    };

    axios
      .post("http://localhost:3001/addApi", form, config)
      .then((res) => {
        if (res.data !== "") {
          alert("added");
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <div className="container col-md-8">
        <div className="card mt-5 shadow-lg">
          <div className="text-center mt-3">
            <h3>Add Api Data</h3>
          </div>
          <div className="card-body">
            <form>
              <label className="mt-2">Category</label>
              <select
                type="text"
                name="Category"
                onChange={getData}
                value={Api.Category}
                className="form-control"
              >
                {apis.map((data) => {
                    return(
                        <option>{data.Name}</option>
                    )
                })}
              </select>
              <label className="mt-2">Name</label>
              <input
                type="text"
                name="Name"
                onChange={getData}
                value={Api.Name}
                className="form-control"
              ></input>
              <label>Description</label>
              <textarea
                rows={5}
                name="Description"
                onChange={getData}
                value={Api.Description}
                className="form-control"
              ></textarea>
              <label className="mt-2">Data</label>
              <input
                type="file"
                name="Img"
                onChange={(ev) => {
                  setApiImage(ev.target.files[0]);
                }}
                className="form-control"
              ></input>

              <button
                onClick={sub}
                className=" shadow-lg form-control btn btn-success mt-4"
              >
                Add Data
              </button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default AddapiData;
