import React, { useState, useEffect } from "react";
import axios from "axios";
import $ from "jquery";
import Uleft from "./Uleft";

const Setting = () => {
  const [uname, setuname] = useState(false);
  useEffect(() => {
    if (sessionStorage.getItem("users") != null) {
      const user = sessionStorage.getItem("users");
      const users = JSON.parse(user);
      setuname(users.uname);
    }
  }, []);
  const [Key, setKey] = useState("");

  const Keys = () => {
    const pattern =
      "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789@#$&";
    let key = "";
    const keysize = 30;
    for (let i = 0; i < keysize; i++) {
      key += pattern.charAt(Math.floor(Math.random() * pattern.length));
    }
    setKey(key);
  };
  const [AdKey, setAdKey] = useState({
    appname: "",
  });
  const getData = (ev) => {
    let name = ev.target.name;
    let value = ev.target.value;

    setAdKey({ ...AdKey, [name]: value });
  };
  const sub = (ev) => {
    ev.preventDefault();
    const form = {
      AppName: AdKey.appname,
      Key: $("#ke").val(),
      username: uname,
    };
    console.log(form);
    axios
      .post(`${process.env.REACT_APP_BACKEND}/addKey`, form)
      .then((res) => {})
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <div>
        <div className="row">
          <div className="col-md-2 setleft shadow-lg mt-3 ms-3">
            <Uleft></Uleft>
          </div>
          <div className="col-md-10">
            <div className="login">
              <div className="container mt-5 keygen">
                <div className="row">
                  <div className="col-md-7">
                    <div className="card">
                      <div className="card-body mt-3">
                        <form>
                          <label>Application Name</label>
                          <input
                            type="text"
                            name="appname"
                            onChange={getData}
                            value={AdKey.appname}
                            className="form-control"
                            placeholder="Enter your project name"
                          ></input>
                          <label className="mt-2">APIs Key</label>
                          <input
                            type="text"
                            name="key"
                            id="ke"
                            readOnly=""
                            contentEditable='false'
                            className="form-control"
                            value={Key}
                            placeholder="key"
                          ></input>
                          <div className="genkey">
                            <a onClick={Keys}>
                              Generate Key
                            </a>
                          </div>
                          <button
                            onClick={sub}
                            className="form-control btn btn-success mt-4"
                          >
                            CREATE
                          </button>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Setting;
