      <h3 className="text-center mt-5 shadow-lg p-2">KEY DETAILS</h3>
        {data.map((data) => {
          return (
            <div className="card mt-5 pt-1 form-control">
            <div className="card-body mt-1" key={data._id}>
              <div>
                Application Name: &nbsp; {data.AppName}
              </div>
              <div>Key: &nbsp; {data.Key}</div>
              <div className="pt-2" style={{color:'red'}} onClick={delet}>Remove</div>
            </div>
            </div>
          );
        })}
    </>
  );
};

