import React from "react";

const ShowApis = ({ apis,perPage,total,setcurrentPage}) => {
    let arr=[]
    for(let i=0;i<Math.ceil(total/perPage);i++)
    {
       arr.push(i)
    }
  return (
    <>
      <div className="mt-5">
        <div className="container p-5">
          <table class="table table-striped">
            <thead>
              <tr>
                <th scope="col">No</th>
                <th scope="col">Category</th>
                <th scope="col">Name</th>
                <th scope="col">Image</th>
                <th scope="col">Description</th>
              </tr>
            </thead>
            <tbody>
              {apis.map((data,index) => {
                  let url=`${process.env.REACT_APP_BACKEND}/getApis/${data.Image}`;
                return (
                  <tr key={data._id}>
                    <th scope="row">{index+1}</th>
                    <td>{data.Category}</td>
                    <td>{data.Name}</td>
                    <td><img src={url}></img></td>
                    <td>{data.Description}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="pagination">
            {
                arr.map((data)=>{
                    return(
                      <div className="pagiBtn">
                      <button className="btn btn-primary" onClick={()=>{setcurretPage(data)}}>{data}</button>
                      </div>
                    )
                })
            }
        </div>
      </div>
    </>
  );
};

export default ShowApis;
