import React, { useEffect,useState} from 'react'
import axios from 'axios'
const Api = () => {
   const [apis,setapis]=useState([]);
  useEffect(()=>{

    const options = {
      method: 'GET',
      url: `http://localhost:3001/getrandomWord`,
      params: {fra: 'true'},
      headers: {
        'X-Key':'mpk@B$m&W0ya8teAv&mUPq17IEz8SK',
      }
    };
    axios.request(options).then((da)=>{
    }).catch((err)=>{
      console.log(err)
    })
   axios.get(`${process.env.REACT_APP_BACKEND}/getapi`).then((res)=>{
        setapis(res.data)
   }).catch((err)=>{
        console.log(err);
   })
  },[])
  return (
    <>
      <div className='container mt-5 apicont'>
         <div className=' shadow-lg apicate py-3'>
           <div>
             <select>
             <option readOnly>Select Category</option>
             {
             apis.map((data)=>{
               return(
               <option>{data.Category}</option>
               )
             })
            }
           </select>
           </div>
           <div><select><option>select category</option><option>select category</option>

           </select></div>
           <div><select><option>select category</option><option>select category</option>

           </select></div>
         </div>

         <div className='mt-5 apicardcont'>

         <div className="row row-cols-1 row-cols-md-4 g-4">

           {
             apis.map((data)=>{
               const url=`${process.env.REACT_APP_BACKEND}/getApis/${data.Image}`
               return(
                <div className='col' key={data._id}>
                  <div className='card shadow-lg'>
                <a href={`/apideatil/${data.Name}`} style={{'textDecoration':'none','color':'black'}}><div className='card text-center'>
                  <h5 className='pt-2'>{data.Name}</h5>
                <img src={url} className=" d-block mx-auto card-img-top apiimg" alt="..." />
                  <div className='card-body'>
                        {data.Description}
                  </div>
                </div>
                </a>
                </div>
              </div>
               )
             })
           }




           </div>
         </div>
      </div>
    </>
  )
}

export default Api
